import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Navbar.css';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const location = useLocation();
  const { currentUser, isMember, isAdmin } = useAuth();

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const closeMenu = () => {
    setIsOpen(false);
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo" onClick={closeMenu}>
          <img src="/images/icon.png" alt="UofT Triathlon Logo" className="navbar-icon" />
          <span className="logo-text">UofT Triathlon</span>
        </Link>

        <div className={`navbar-menu ${isOpen ? 'active' : ''}`}>
          <Link 
            to="/" 
            className={`navbar-link ${isActive('/') ? 'active' : ''}`}
            onClick={closeMenu}
          >
            Home
          </Link>
          <Link 
            to="/schedule" 
            className={`navbar-link ${isActive('/schedule') ? 'active' : ''}`}
            onClick={closeMenu}
          >
            Schedule
          </Link>

          <Link 
            to="/join-us" 
            className={`navbar-link ${isActive('/join-us') ? 'active' : ''}`}
            onClick={closeMenu}
          >
            Join Us
          </Link>

                    <Link
            to="/coaches-exec"
            className={`navbar-link ${isActive('/coaches-exec') ? 'active' : ''}`}
            onClick={closeMenu}
          >
            Coaches & Exec
          </Link>
          <Link
            to="/admin"
            className={`navbar-link ${isActive('/admin') ? 'active' : ''}`}
            onClick={closeMenu}
          >
            Admin
          </Link>

          <Link 
            to="/forum" 
            className={`navbar-link ${isActive('/forum') ? 'active' : ''}`}
            onClick={closeMenu}
          >
            Forum
          </Link>
          <Link 
            to="/login" 
            className={`navbar-link ${isActive('/login') ? 'active' : ''}`}
            onClick={closeMenu}
          >
            Login
          </Link>
        </div>

        <div className="navbar-toggle" onClick={toggleMenu}>
          <span className={`bar ${isOpen ? 'active' : ''}`}></span>
          <span className={`bar ${isOpen ? 'active' : ''}`}></span>
          <span className={`bar ${isOpen ? 'active' : ''}`}></span>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
