import React from 'react';
import { useAuth } from '../context/AuthContext';
import './Footer.css';

const Footer = () => {
  const { currentUser, logout } = useAuth();

  const handleLogout = () => {
    logout();
  };

  return (
    <footer className="footer">
      <div className="container">
        <div className="footer-content">
          <div className="footer-section">
            <h3>UofT Triathlon Team</h3>
            <p>Excellence in swimming, cycling, and running since 2009.</p>
            <div className="social-links">
              <a href="mailto:triathlon@utoronto.ca" className="social-link" title="Email">📧</a>
              <a href="#" className="social-link" title="Facebook">📱</a>
              <a href="https://www.instagram.com/uoft_tri_club/" target="_blank" rel="noopener noreferrer" className="social-link" title="Instagram">📷</a>
            </div>
          </div>

          <div className="footer-section">
            <h4>Quick Links</h4>
            <ul className="footer-links">
              <li><a href="/">Home</a></li>
              <li><a href="/schedule">Schedule</a></li>
              <li><a href="/coaches-exec">Coaches & Exec</a></li>
              <li><a href="/forum">Forum</a></li>
            </ul>
          </div>

          <div className="footer-section">
            <h4>Contact</h4>
            <p>📍 UofT Athletic Centre</p>
            <p>📧 triathlon@utoronto.ca</p>
            <p>📱 (416) 978-1234</p>
          </div>

          <div className="footer-section">
            <h4>Account</h4>
            {currentUser ? (
              <div className="user-info">
                <p>Welcome, {currentUser.name}!</p>
                <button onClick={handleLogout} className="logout-btn">
                  Logout
                </button>
              </div>
            ) : (
              <p>Sign in to access all features</p>
            )}
          </div>
        </div>

        <div className="footer-bottom">
          <p>&copy; 2024 UofT Triathlon Team. All rights reserved.</p>
          <p>Built with ❤️ for the triathlon community</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
