import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './CoachesExec.css';

const CoachesExec = () => {
  const location = useLocation();
  const [teamMembers, setTeamMembers] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Load team members from backend API
  useEffect(() => {
    const loadTeamMembers = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://localhost:5001/api/profiles');

        if (!response.ok) {
          throw new Error('Failed to fetch team members');
        }

        const data = await response.json();

        // Convert array to object with id as key
        const membersObject = {};
        if (Array.isArray(data.teamMembers)) {
          data.teamMembers.forEach(member => {
            // Convert relative image URLs to full URLs for display
            membersObject[member.id] = {
              ...member,
              image: member.image && member.image.startsWith('/uploads/') 
                ? `http://localhost:5001${member.image}` 
                : member.image
            };
          });
          console.log('✅ Converted', data.teamMembers.length, 'team members to object');
        } else {
          console.error('❌ Expected array but got:', typeof data.teamMembers, data.teamMembers);
        }

        setTeamMembers(membersObject);
        setError(null);
      } catch (error) {
        console.error('Error loading team members:', error);
        setError('Failed to load team members');
      } finally {
        setLoading(false);
      }
    };

    loadTeamMembers();
  }, []);

  // Refresh team members when location changes (when navigating back to this page)
  useEffect(() => {
    if (!loading) {
      const refreshTeamMembers = async () => {
        try {
          const response = await fetch('http://localhost:5001/api/profiles');

          if (response.ok) {
            const data = await response.json();

            // Convert array to object with id as key
            const membersObject = {};
            if (Array.isArray(data.teamMembers)) {
              data.teamMembers.forEach(member => {
                // Convert relative image URLs to full URLs for display
                membersObject[member.id] = {
                  ...member,
                  image: member.image && member.image.startsWith('/uploads/') 
                    ? `http://localhost:5001${member.image}` 
                    : member.image
                };
              });
            }

            setTeamMembers(membersObject);
          }
        } catch (error) {
          console.error('Error refreshing team members:', error);
        }
      };

      refreshTeamMembers();
    }
  }, [location.pathname, loading]);

  // Add periodic refresh to keep data in sync
  useEffect(() => {
    if (!loading) {
      const interval = setInterval(async () => {
        try {
          const response = await fetch('http://localhost:5001/api/profiles');

          if (response.ok) {
            const data = await response.json();

            // Convert array to object with id as key
            const membersObject = {};
            if (Array.isArray(data.teamMembers)) {
              data.teamMembers.forEach(member => {
                // Convert relative image URLs to full URLs for display
                membersObject[member.id] = {
                  ...member,
                  image: member.image && member.image.startsWith('/uploads/') 
                    ? `http://localhost:5001${member.image}` 
                    : member.image
                };
              });
            }

            // Only update if data has actually changed
            setTeamMembers(prev => {
              if (JSON.stringify(prev) !== JSON.stringify(membersObject)) {
                console.log('🔄 Team members data updated, refreshing display');
                return membersObject;
              }
              return prev;
            });
          }
        } catch (error) {
          console.error('Error during periodic refresh:', error);
        }
      }, 5000); // Refresh every 5 seconds

      return () => clearInterval(interval);
    }
  }, [loading]);

  if (loading) {
    return (
      <div className="coaches-exec-container">
        <div className="container">
          <div className="loading-state">
            <h2>Loading team members...</h2>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="coaches-exec-container">
        <div className="container">
          <div className="error-state">
            <h2>Error loading team members</h2>
            <p>{error}</p>
            <button onClick={() => window.location.reload()}>Try Again</button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="coaches-exec-container">
      <div className="container">
        <h1 className="section-title">Coaches & Executive Team</h1>

        {/* Coaches Section */}
        <div className="coaches-section">
          <h2 className="section-subtitle">Our Coaches</h2>
          <div className="coaches-grid">
            <div className="coach-card">
              <div className="coach-avatar">
                <div className="coach-photo">
                  <img 
                    src={teamMembers['swim-coach']?.image || "/images/swimcoach.png"} 
                    alt="Justin Konik - Swim Coach"
                  />
                </div>
                <span className="coach-emoji">🏊‍♂️</span>
              </div>
              <h3>Swim Coach</h3>
              <p className="coach-name">
                <Link to="/profile/swim-coach/justin-konik" className="profile-link">
                  Justin Konik
                </Link>
              </p>
              <p className="coach-bio">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris.
              </p>
              <div className="coach-contact">
                <p><strong>Contact:</strong> [Email]</p>
              </div>
            </div>

            <div className="coach-card">
              <div className="coach-avatar">
                <div className="coach-photo">
                  <img 
                      src="/images/team-photo1.jpeg" 
                      alt="UofT Triathlon Team - Photo 1"
                    />
                </div>
                <span className="coach-emoji">🚴‍♂️</span>
              </div>
              <h3>Run Coach</h3>
              <p className="coach-name">[Coach Name]</p>
              <p className="coach-bio">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris.
              </p>
              <div className="coach-contact">
                <p><strong>Contact:</strong> [Email]</p>
              </div>
            </div>
          </div>
        </div>

        {/* Executive Team Section */}
        <div className="exec-team-section">
          <h2 className="section-subtitle">Executive Team</h2>
          <div className="exec-grid">
            <div className="exec-card">
              <div className="exec-avatar">
                <div className="exec-photo">
                  <img 
                    src={teamMembers['co-president']?.image || "/images/team-photo1.jpeg"} 
                    alt="Jade Clement - Co-President"
                  />
                </div>
                <span className="exec-emoji">👑</span>
              </div>
              <h3>Co-President</h3>
              <p className="exec-name">
                <Link to="/profile/co-president/jade-clement" className="profile-link">
                  Jade Clement
                </Link>
              </p>
              <p className="exec-bio">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
              </p>
            </div>

            <div className="exec-card">
              <div className="exec-avatar">
                <div className="exec-photo">
                  <span className="photo-placeholder">Photo</span>
                </div>
                <span className="exec-emoji">👑</span>
              </div>
              <h3>Co-President</h3>
              <p className="exec-name">
                <Link to="/profile/co-president-2/marlene-garijo" className="profile-link">
                  Marlene Garijo
                </Link>
              </p>
              <p className="exec-bio">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
              </p>
            </div>

            <div className="exec-card">
              <div className="exec-avatar">
                <div className="exec-photo">
                  <img 
                    src={teamMembers['treasurer']?.image || "/images/exec_treasurer.jpg"} 
                    alt="Edward Ing - Treasurer"
                  />
                </div>
                <span className="exec-emoji">💰</span>
              </div>
              <h3>Treasurer</h3>
              <p className="exec-name">
                <Link to="/profile/treasurer/edward-ing" className="profile-link">
                  Edward Ing
                </Link>
              </p>
              <p className="exec-bio">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
              </p>
            </div>

            <div className="exec-card">
              <div className="exec-avatar">
                <div className="exec-photo">
                  <span className="photo-placeholder">Photo</span>
                </div>
                <span className="exec-emoji">📝</span>
              </div>
              <h3>Secretary</h3>
              <p className="exec-name">
                <Link to="/profile/secretary/lauren-williams" className="profile-link">
                  Lauren Williams
                </Link>
              </p>
              <p className="exec-bio">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
              </p>
            </div>

            <div className="exec-card">
              <div className="exec-avatar">
                <div className="exec-photo">
                  <span className="photo-placeholder">Photo</span>
                </div>
                <span className="exec-emoji">🎉</span>
              </div>
              <h3>Social Coordinator/Recruitment</h3>
              <p className="exec-name">
                <Link to="/profile/social-coordinator/katy-tiper" className="profile-link">
                  Katy Tiper
                </Link>
              </p>
              <p className="exec-bio">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
              </p>
            </div>

            <div className="exec-card">
              <div className="exec-avatar">
                <div className="exec-photo">
                  <img 
                    src={teamMembers['social-media']?.image || "/images/exec_socialmedia.jpg"} 
                    alt="Paulette Dalton - Social Media Manager"
                  />
                </div>
                <span className="exec-emoji">📱</span>
              </div>
              <h3>Social Media Manager</h3>
              <p className="exec-name">
                <Link to="/profile/social-media/paulette-dalton" className="profile-link">
                  Paulette Dalton
                </Link>
              </p>
              <p className="exec-bio">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
              </p>
            </div>

            <div className="exec-card">
              <div className="exec-avatar">
                <div className="exec-photo">
                  <span className="photo-placeholder">Photo</span>
                </div>
                <span className="exec-emoji">💻</span>
              </div>
              <h3>Webmaster</h3>
              <p className="exec-name">
                <Link to="/profile/webmaster/ilan-gofman" className="profile-link">
                  Ilan Gofman
                </Link>
              </p>
              <p className="exec-bio">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
              </p>
            </div>

            <div className="exec-card">
              <div className="exec-avatar">
                <div className="exec-photo">
                  <span className="photo-placeholder">Photo</span>
                </div>
                <span className="exec-emoji">🏃‍♂️</span>
              </div>
              <h3>Workout/Race Coordinator</h3>
              <p className="exec-name">[Name]</p>
              <p className="exec-bio">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
              </p>
            </div>
          </div>
        </div>

        {/* Contact Section */}
        <div className="contact-section">
          <h2 className="section-subtitle">Get in Touch</h2>
          <p className="contact-info">
            For general inquiries about the club, please email <a href="mailto:info@uofttriathlon.com">info@uofttriathlon.com</a>.
          </p>
          <p className="contact-info">
            For specific questions about workouts or races, please contact the respective coordinator listed above.
          </p>
        </div>
      </div>
    </div>
  );
};

export default CoachesExec;
