import React, { useState, useContext } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Profile.css';

const Profile = () => {
  const { role, name } = useParams();
  const { currentUser, isAdmin } = useAuth();
  const [editMode, setEditMode] = useState(false);
  const [editedBio, setEditedBio] = useState('');
  const [editedImage, setEditedImage] = useState('');
  const [saving, setSaving] = useState(false);

  // Team member data - you can expand this with more details
  const teamMembers = {
    'swim-coach': {
      name: 'Justin Konik',
      role: 'Swim Coach',
      image: '/images/swimcoach.png',
      bio: `Justin Konik is our dedicated swim coach with over 10 years of experience in competitive swimming and triathlon coaching. He specializes in stroke technique, endurance training, and helping athletes overcome their fear of open water swimming.

Justin's coaching philosophy focuses on building confidence in the water while developing efficient swimming mechanics. He has coached athletes from complete beginners to elite competitors, and his personalized approach ensures each swimmer reaches their full potential.

When not coaching, Justin competes in open water swimming events and enjoys helping new triathletes discover their love for the sport. He believes that swimming should be both challenging and enjoyable, and he's committed to making every training session productive and fun.`
    },
    'co-president': {
      name: 'Jade Clement',
      role: 'Co-President',
      image: '/images/team-photo1.jpeg', // Using one of your team photos
      bio: `Jade Clement is one of our Co-Presidents, leading the UofT Triathlon Club with passion and dedication. With a strong background in triathlon and a commitment to building community, Jade works tirelessly to ensure every member feels welcome and supported.

Jade's leadership focuses on creating inclusive training environments, organizing social events, and fostering connections between club members. She believes that triathlon is not just about individual achievement, but about building lasting friendships and supporting each other's goals.

Under Jade's leadership, the club has grown significantly, with more members than ever before participating in races, training sessions, and social events. Her vision for the club emphasizes both athletic excellence and community building.`
    },
    'co-president-2': {
      name: 'Marlene Garijo',
      role: 'Co-President',
      image: '/images/team-photo2.jpeg', // Using one of your team photos
      bio: `Marlene Garijo serves as Co-President alongside Jade, bringing her unique perspective and energy to club leadership. With a background in competitive sports and event organization, Marlene excels at creating memorable experiences for club members.

Marlene's strengths lie in strategic planning, member engagement, and ensuring the club operates smoothly behind the scenes. She works closely with the executive team to implement new initiatives and maintain the high standards that make our club special.

Her passion for triathlon extends beyond personal achievement to helping others discover their potential in the sport. Marlene believes that every member has something valuable to contribute to the club, and she works to create opportunities for everyone to get involved.`
    },
    'treasurer': {
      name: 'Edward Ing',
      role: 'Treasurer',
      image: '/images/exec_treasurer.jpg',
      bio: `Edward Ing serves as our club's Treasurer, managing the financial health of the UofT Triathlon Club with precision and care. With a background in finance and a passion for triathlon, Edward ensures that club resources are allocated effectively to benefit all members.

Edward's responsibilities include budgeting for training equipment, race entries, social events, and club operations. He works closely with the executive team to make strategic financial decisions that support the club's growth and member experience.

Beyond his financial duties, Edward is an active participant in club activities and serves as a mentor to newer members. He believes that sound financial management enables the club to provide better opportunities for all members to develop their triathlon skills.`
    },
    'secretary': {
      name: 'Lauren Williams',
      role: 'Secretary',
      image: '/images/team-photo3.jpeg', // Using one of your team photos
      bio: `Lauren Williams is our club Secretary, responsible for maintaining clear communication and record-keeping within the club. With excellent organizational skills and attention to detail, Lauren ensures that important information reaches all members and that club activities are well-documented.

Lauren's role includes managing club communications, taking meeting minutes, maintaining membership records, and coordinating with other executive members to keep everyone informed. Her work behind the scenes is crucial to the smooth operation of the club.

Lauren is passionate about creating an inclusive environment where every member feels heard and valued. She believes that clear communication is the foundation of a strong community, and she works diligently to maintain those standards.`
    },
    'social-coordinator': {
      name: 'Katy Tiper',
      role: 'Social Coordinator/Recruitment',
      image: '/images/team-photo4.jpg', // Using one of your team photos
      bio: `Katy Tiper serves as our Social Coordinator and Recruitment specialist, bringing energy and creativity to building our club community. With a natural ability to connect people and organize engaging events, Katy plays a vital role in making our club welcoming to new and existing members.

Katy's responsibilities include planning social events, organizing team building activities, and developing recruitment strategies to grow our membership. She believes that the social aspect of triathlon is just as important as the athletic component, and she works to create opportunities for members to bond and build friendships.

Her approach to recruitment focuses on showcasing the inclusive, supportive nature of our club and helping potential members see how they can fit in, regardless of their experience level. Katy believes that everyone has a place in triathlon, and she's committed to helping them find it.`
    },
    'social-media': {
      name: 'Paulette Dalton',
      role: 'Social Media Manager',
      image: '/images/exec_socialmedia.jpg',
      bio: `Paulette Dalton is our Social Media Manager, responsible for building our club's online presence and connecting with the broader triathlon community. With expertise in digital marketing and a passion for storytelling, Paulette shares our club's journey and achievements with the world.

Paulette's role includes managing all social media platforms, creating engaging content that showcases our members' accomplishments, and building relationships with other triathlon clubs and organizations. She believes that social media is a powerful tool for building community, sharing knowledge, and inspiring others to join the sport.

Her content strategy focuses on highlighting the diverse experiences of our members, from first-time triathletes to seasoned competitors, and showcasing the supportive environment that makes our club special. Paulette believes that every member has a story worth sharing.`
    },
    'webmaster': {
      name: 'Ilan Gofman',
      role: 'Webmaster',
      image: '/images/icon.png', // Using your icon as placeholder
      bio: `Ilan Gofman serves as our club's Webmaster, managing our digital infrastructure and ensuring that our online presence effectively serves our members. With technical expertise and a commitment to user experience, Ilan maintains the systems that keep our club connected and informed.

Ilan's responsibilities include maintaining our website, managing member databases, and implementing digital solutions that improve club operations. He works closely with the executive team to identify technological needs and develop solutions that enhance the member experience.

Beyond his technical duties, Ilan is passionate about using technology to build community and make information accessible to all members. He believes that well-designed digital tools can strengthen relationships within the club and make it easier for members to stay engaged and informed.`
    }
  };

  // Find the team member based on the URL parameters
  const memberKey = `${role}-${name}`.toLowerCase().replace(/\s+/g, '-');

  // Alternative lookup methods for different URL formats
  const alternativeKeys = [
    memberKey,
    role,
    name,
    `${role}-${name}`.toLowerCase(),
    `${role}-${name}`.toLowerCase().replace(/\s+/g, '-'),
    `${role}-${name}`.toLowerCase().replace(/[^a-z0-9-]/g, '')
  ];

  const member = alternativeKeys.reduce((found, key) => {
    return found || teamMembers[key];
  }, null);

  const handleEdit = () => {
    setEditedBio(member.bio);
    setEditedImage(member.image);
    setEditMode(true);
  };

  const handleSave = () => {
    setSaving(true);
    // In a real app, you'd save to a database here
    // For now, we'll just update the local state
    member.bio = editedBio;
    member.image = editedImage;
    setEditMode(false);
    setSaving(false);
  };

  const handleCancel = () => {
    setEditMode(false);
    setEditedBio('');
    setEditedImage('');
  };

  if (!member) {
    return (
      <div className="profile-container">
        <div className="container">
          <h1>Profile Not Found</h1>
          <p>Sorry, we couldn't find the profile you're looking for.</p>
          <Link to="/coaches-exec" className="back-link">← Back to Team</Link>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="container">
        <Link to="/coaches-exec" className="back-link">← Back to Team</Link>

        <div className="profile-content">
          <div className="profile-image-section">
            <img 
              src={editMode ? (editedImage || member.image) : member.image} 
              alt={`${member.name} - ${member.role}`}
              className="profile-image"
            />
          </div>

          <div className="profile-info-section">
            <div className="profile-header">
              <h1 className="profile-name">{member.name}</h1>
              <h2 className="profile-role">{member.role}</h2>
              {isAdmin && (
                <div className="profile-actions">
                  {editMode ? (
                    <div className="edit-actions">
                      <button 
                        className="btn btn-primary" 
                        onClick={handleSave}
                        disabled={saving}
                      >
                        {saving ? 'Saving...' : 'Save Changes'}
                      </button>
                      <button 
                        className="btn btn-secondary" 
                        onClick={handleCancel}
                        disabled={saving}
                      >
                        Cancel
                      </button>
                    </div>
                  ) : (
                    <button 
                      className="btn btn-edit" 
                      onClick={handleEdit}
                    >
                      Edit Bio
                    </button>
                  )}
                </div>
              )}
            </div>

            {editMode ? (
              <div className="edit-form">
                <div className="image-upload-section">
                  <label htmlFor="profile-image" className="image-upload-label">
                    <div className="image-preview">
                      <img 
                        src={editedImage || member.image} 
                        alt={`${member.name} - ${member.role}`}
                        className="preview-image"
                      />
                      <div className="image-overlay">
                        <span>Click to change image</span>
                      </div>
                    </div>
                  </label>
                  <input
                    id="profile-image"
                    type="file"
                    accept="image/*"
                    onChange={(e) => {
                      const file = e.target.files[0];
                      if (file) {
                        const imageUrl = URL.createObjectURL(file);
                        setEditedImage(imageUrl);
                      }
                    }}
                    style={{ display: 'none' }}
                  />
                  <p className="image-upload-help">
                    Click the image above to upload a new profile picture
                  </p>
                </div>

                <div className="bio-edit-section">
                  <label htmlFor="bio-textarea" className="form-label">Bio:</label>
                  <textarea
                    id="bio-textarea"
                    value={editedBio}
                    onChange={(e) => setEditedBio(e.target.value)}
                    className="bio-textarea"
                    rows="10"
                    placeholder="Enter the team member's bio..."
                  />
                </div>
              </div>
            ) : (
              <div className="profile-bio">
                {member.bio.split('\n\n').map((paragraph, index) => (
                  <p key={index}>{paragraph}</p>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
