import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import './Forum.css';

const Forum = () => {
  const { currentUser, isMember, isExec, getUserRole } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('workouts');
  const [workoutPosts, setWorkoutPosts] = useState([]);
  const [eventPosts, setEventPosts] = useState([]);
  const [newWorkout, setNewWorkout] = useState('');
  const [newEvent, setNewEvent] = useState('');
  const [loading, setLoading] = useState(true);

  const API_BASE_URL = 'http://localhost:5001/api';

  useEffect(() => {
    if (!currentUser) {
      navigate('/login');
      return;
    }

    // Check if user has member access or higher
    if (!isMember(currentUser)) {
      navigate('/login');
      return;
    }

    // Load forum posts from backend API
    loadForumPosts();
  }, [currentUser, navigate, isMember]);

  const loadForumPosts = async () => {
    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      // Load workout posts
      const workoutResponse = await fetch(`${API_BASE_URL}/forum/workouts`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (workoutResponse.ok) {
        const workoutData = await workoutResponse.json();
        setWorkoutPosts(workoutData.posts || []);
      }

      // Load event posts
      const eventResponse = await fetch(`${API_BASE_URL}/forum/events`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (eventResponse.ok) {
        const eventData = await eventResponse.json();
        setEventPosts(eventData.posts || []);
      }
    } catch (error) {
      console.error('Error loading forum posts:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitPost = async (e) => {
    e.preventDefault();
    if (!newPost.trim()) return;

    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      const response = await fetch(`${API_BASE_URL}/forum/posts`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          content: newPost
        })
      });

      if (response.ok) {
        const newPostData = await response.json();
        setPosts([newPostData.post, ...posts]);
        setNewPost('');
      } else {
        console.error('Failed to create post');
      }
    } catch (error) {
      console.error('Error creating post:', error);
    }
  };

  const handleLike = async (postId) => {
    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      const response = await fetch(`${API_BASE_URL}/forum/posts/${postId}/like`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        // Reload posts to get updated like count
        loadForumPosts();
      } else {
        console.error('Failed to like post');
      }
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!currentUser) {
    return null;
  }

  return (
    <div className="forum-container">
      <div className="container">
        <h1 className="section-title">Team Forum</h1>
        <p className="section-subtitle">Connect with your teammates and discuss training, races, and more!</p>

        {/* Create Post Form */}
        <div className="create-post">
          <form onSubmit={handleSubmitPost}>
            <textarea
              value={newPost}
              onChange={(e) => setNewPost(e.target.value)}
              placeholder="Share something with your team..."
              rows="4"
              maxLength="500"
            />
            <div className="post-actions">
              <span className="char-count">{newPost.length}/500</span>
              <button type="submit" className="btn">Post</button>
            </div>
          </form>
        </div>

        {/* Posts List */}
        <div className="posts-list">
          {posts.length === 0 ? (
            <div className="no-posts">
              <p>No posts yet. Be the first to share something!</p>
            </div>
          ) : (
            posts.map(post => (
              <div key={post.id} className="post-card">
                <div className="post-header">
                  <div className="post-author">
                    <div className="author-avatar">
                      {post.author.charAt(0).toUpperCase()}
                    </div>
                    <div className="author-info">
                      <span className="author-name">{post.author}</span>
                      <span className="post-time">
                        {new Date(post.timestamp).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                </div>
                <div className="post-content">
                  {post.content}
                </div>
                <div className="post-footer">
                  <button 
                    onClick={() => handleLike(post.id)}
                    className="like-btn"
                  >
                    👍 {post.likes}
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Forum;
