import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import './Forum.css';

const Forum = () => {
  const { currentUser, isMember, isExec, getUserRole } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('workouts');
  const [workoutPosts, setWorkoutPosts] = useState([]);
  const [eventPosts, setEventPosts] = useState([]);
  const [newWorkout, setNewWorkout] = useState('');
  const [newEvent, setNewEvent] = useState('');
  const [loading, setLoading] = useState(true);
  const [showWorkoutForm, setShowWorkoutForm] = useState(false);
  const [showEventForm, setShowEventForm] = useState(false);
  const [workoutForm, setWorkoutForm] = useState({
    title: '',
    type: 'spin',
    date: '',
    content: ''
  });

  const API_BASE_URL = 'http://localhost:5001/api';

  useEffect(() => {
    if (!currentUser) {
      navigate('/login');
      return;
    }

    // Check if user has member access or higher
    if (!isMember(currentUser)) {
      navigate('/login');
      return;
    }

    // Load forum posts from backend API
    loadForumPosts();
  }, [currentUser, navigate, isMember]);

  const loadForumPosts = async () => {
    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      // Load workout posts
      const workoutResponse = await fetch(`${API_BASE_URL}/forum/workouts`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (workoutResponse.ok) {
        const workoutData = await workoutResponse.json();
        setWorkoutPosts(workoutData.posts || []);
      }

      // Load event posts
      const eventResponse = await fetch(`${API_BASE_URL}/forum/events`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (eventResponse.ok) {
        const eventData = await eventResponse.json();
        setEventPosts(eventData.posts || []);
      }
    } catch (error) {
      console.error('Error loading forum posts:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitWorkout = async (e) => {
    e.preventDefault();
    if (!workoutForm.title.trim() || !workoutForm.date || !workoutForm.content.trim()) return;

    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      const response = await fetch(`${API_BASE_URL}/forum/workouts`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          title: workoutForm.title.trim(),
          workoutType: workoutForm.type,
          workoutDate: workoutForm.date,
          content: workoutForm.content.trim(),
          type: 'workout'
        })
      });

      if (response.ok) {
        const newPostData = await response.json();
        setWorkoutPosts([newPostData.post, ...workoutPosts]);
        setWorkoutForm({
          title: '',
          type: 'spin',
          date: '',
          content: ''
        });
        setShowWorkoutForm(false);
      } else {
        console.error('Failed to create workout post');
      }
    } catch (error) {
      console.error('Error creating workout post:', error);
    }
  };

  const handleSubmitEvent = async (e) => {
    e.preventDefault();
    if (!newEvent.trim()) return;

    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      const response = await fetch(`${API_BASE_URL}/forum/events`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          content: newEvent,
          type: 'event'
        })
      });

      if (response.ok) {
        const newPostData = await response.json();
        setEventPosts([newPostData.post, ...eventPosts]);
        setNewEvent('');
        setShowEventForm(false);
      } else {
        console.error('Failed to create event post');
      }
    } catch (error) {
      console.error('Error creating event post:', error);
    }
  };

  const handleLike = async (postId) => {
    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      const response = await fetch(`${API_BASE_URL}/forum/posts/${postId}/like`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        // Reload posts to get updated like count
        loadForumPosts();
      } else {
        console.error('Failed to like post');
      }
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!currentUser) {
    return null;
  }

  return (
    <div className="forum-container">
      <div className="container">
        <h1 className="section-title">Team Forum</h1>
        <p className="section-subtitle">Connect with your teammates and discuss training, races, and more!</p>

        {/* Forum Tabs */}
        <div className="forum-tabs">
          <button 
            className={`tab-button ${activeTab === 'workouts' ? 'active' : ''}`}
            onClick={() => setActiveTab('workouts')}
          >
            üèÉ‚Äç‚ôÇÔ∏è Workouts
          </button>
          <button 
            className={`tab-button ${activeTab === 'events' ? 'active' : ''}`}
            onClick={() => setActiveTab('events')}
          >
            üéâ Events
          </button>
        </div>

        {/* Workouts Section */}
        {activeTab === 'workouts' && (
          <div className="workouts-section">
            <div className="section-header">
              <h2>Workout Posts</h2>
              {isExec(currentUser) && (
                <button 
                  className="new-post-btn"
                  onClick={() => setShowWorkoutForm(true)}
                >
                  + New Workout
                </button>
              )}
            </div>

            {workoutPosts.length === 0 ? (
              <p className="no-posts">No workout posts yet.</p>
            ) : (
              <div className="posts-list">
                {workoutPosts.map(post => (
                  <div key={post.id} className="post-card workout-post">
                    {post.title && (
                      <div className="workout-title">
                        <h2>
                          <a href={`/workout/${post.id}`} className="workout-title-link">
                            {post.title}
                          </a>
                        </h2>
                      </div>
                    )}

                    <div className="workout-author">
                      <span className="author-name">Posted by {post.author_name}</span>
                    </div>

                    {post.workout_type && (
                      <div className="workout-meta">
                        <span className="workout-type-badge">{post.workout_type}</span>
                        {post.workout_date && (
                          <span className="workout-date">
                            üìÖ {new Date(post.workout_date).toLocaleDateString()}
                          </span>
                        )}
                      </div>
                    )}

                    <div className="post-content">{post.content}</div>
                    <div className="post-footer">
                      <button 
                        onClick={() => handleLike(post.id)}
                        className="like-btn"
                      >
                        üëç {post.likes || 0}
                      </button>
                      <button className="signup-btn">Sign Up</button>
                      <button className="comment-btn">Comment</button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {/* Events Section */}
        {activeTab === 'events' && (
          <div className="events-section">
            <div className="section-header">
              <h2>Event Posts</h2>
              {isExec(currentUser) && (
                <button 
                  className="new-post-btn"
                  onClick={() => setShowEventForm(true)}
                >
                  + New Event
                </button>
              )}
            </div>

            {eventPosts.length === 0 ? (
              <p className="no-posts">No event posts yet.</p>
            ) : (
              <div className="posts-list">
                {eventPosts.map(post => (
                  <div key={post.id} className="post-card event-post">
                    <div className="post-header">
                      <div className="post-author">
                        <div className="author-avatar">
                          {post.author_name.charAt(0).toUpperCase()}
                        </div>
                        <div className="author-info">
                          <span className="author-name">{post.author_name}</span>
                          <span className="post-time">
                            {new Date(post.created_at).toLocaleDateString()}
                          </span>
                        </div>
                      </div>
                      <span className="post-type">Event</span>
                    </div>
                    <div className="post-content">{post.content}</div>
                    <div className="post-footer">
                      <button 
                        onClick={() => handleLike(post.id)}
                        className="like-btn"
                      >
                        üëç {post.likes || 0}
                      </button>
                      <div className="rsvp-buttons">
                        <button className="rsvp-btn going">Going</button>
                        <button className="rsvp-btn maybe">Maybe</button>
                        <button className="rsvp-btn not-going">Not Going</button>
                      </div>
                      <button className="comment-btn">Comment</button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {/* Workout Creation Modal */}
        {showWorkoutForm && (
          <div className="modal-overlay">
            <div className="modal">
              <h2>Create New Workout Post</h2>
              <form onSubmit={handleSubmitWorkout}>
                <div className="form-group">
                  <label>Workout Title:</label>
                  <input
                    type="text"
                    value={workoutForm.title}
                    onChange={(e) => setWorkoutForm({...workoutForm, title: e.target.value})}
                    placeholder="e.g., Tuesday Morning Swim, Weekend Long Run"
                    maxLength="100"
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Workout Type:</label>
                  <select
                    value={workoutForm.type}
                    onChange={(e) => setWorkoutForm({...workoutForm, type: e.target.value})}
                    required
                  >
                    <option value="spin">Spin</option>
                    <option value="outdoor-ride">Outdoor Ride</option>
                    <option value="run">Run</option>
                    <option value="swim">Swim</option>
                    <option value="brick">Brick (Bike + Run)</option>
                    <option value="other">Other</option>
                  </select>
                </div>

                <div className="form-group">
                  <label>Workout Date:</label>
                  <input
                    type="date"
                    value={workoutForm.date}
                    onChange={(e) => setWorkoutForm({...workoutForm, date: e.target.value})}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Additional Details:</label>
                  <textarea
                    value={workoutForm.content}
                    onChange={(e) => setWorkoutForm({...workoutForm, content: e.target.value})}
                    placeholder="Describe the workout, time, location, requirements, what to bring..."
                    rows="4"
                    maxLength="500"
                    required
                  />
                  <small className="char-count">{workoutForm.content.length}/500</small>
                </div>

                <div className="modal-actions">
                  <button type="submit" className="btn btn-primary">Post Workout</button>
                  <button type="button" className="btn btn-secondary" onClick={() => setShowWorkoutForm(false)}>Cancel</button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Event Creation Modal */}
        {showEventForm && (
          <div className="modal-overlay">
            <div className="modal">
              <h2>Create New Event Post</h2>
              <form onSubmit={handleSubmitEvent}>
                <div className="form-group">
                  <label>Event Details:</label>
                  <textarea
                    value={newEvent}
                    onChange={(e) => setNewEvent(e.target.value)}
                    placeholder="Describe the event, date, time, location, what to bring..."
                    rows="4"
                    maxLength="500"
                    required
                  />
                  <small className="char-count">{newEvent.length}/500</small>
                </div>
                <div className="modal-actions">
                  <button type="submit" className="btn btn-primary">Post Event</button>
                  <button type="button" className="btn btn-secondary" onClick={() => setShowEventForm(false)}>Cancel</button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Forum;
