import React, { useState, useEffect, useContext } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import './Profile.css';

const Profile = () => {
  const { currentUser, updateUser } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [profile, setProfile] = useState({
    name: '',
    email: '',
    bio: '',
    profilePicture: null,
    profilePictureUrl: ''
  });
  const [editMode, setEditMode] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');

  const API_BASE_URL = 'http://localhost:5001/api';

  useEffect(() => {
    if (!currentUser) {
      navigate('/login');
      return;
    }
    loadProfile();
  }, [currentUser, navigate]);

  const loadProfile = async () => {
    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      const response = await fetch(`${API_BASE_URL}/users/profile`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const userData = await response.json();
        setProfile({
          name: userData.name || '',
          email: userData.email || '',
          bio: userData.bio || '',
          profilePicture: null,
          profilePictureUrl: userData.profilePictureUrl || ''
        });
        setPreviewUrl(userData.profilePictureUrl || '');
      } else {
        console.error('Failed to load profile');
      }
    } catch (error) {
      console.error('Error loading profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      const url = URL.createObjectURL(file);
      setPreviewUrl(url);
    }
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      // Create FormData for file upload
      const formData = new FormData();
      formData.append('name', profile.name);
      formData.append('email', profile.email);
      formData.append('bio', profile.bio);
      if (selectedFile) {
        formData.append('profilePicture', selectedFile);
      }

      const response = await fetch(`${API_BASE_URL}/users/profile`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData
      });

      if (response.ok) {
        const updatedUser = await response.json();
        setProfile(prev => ({
          ...prev,
          profilePictureUrl: updatedUser.profilePictureUrl || prev.profilePictureUrl
        }));
        setPreviewUrl(updatedUser.profilePictureUrl || previewUrl);
        setEditMode(false);
        setSelectedFile(null);

        // Update the current user in context
        if (updateUser) {
          updateUser(updatedUser);
        }
      } else {
        console.error('Failed to update profile');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleCancel = () => {
    setEditMode(false);
    setSelectedFile(null);
    setPreviewUrl(profile.profilePictureUrl || '');
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!currentUser) {
    return null;
  }

  return (
    <div className="profile-container">
      <div className="container">
        <div className="profile-header">
          <h1>My Profile</h1>
          <p>Manage your personal information and profile picture</p>
        </div>

        <div className="profile-content">
          <div className="profile-picture-section">
            <div className="profile-picture">
              {previewUrl ? (
                <img src={previewUrl} alt="Profile" />
              ) : (
                <div className="profile-picture-placeholder">
                  <span>📷</span>
                </div>
              )}
            </div>

            {editMode && (
              <div className="picture-upload">
                <label htmlFor="profile-picture" className="upload-btn">
                  Choose Photo
                </label>
                <input
                  id="profile-picture"
                  type="file"
                  accept="image/*"
                  onChange={handleFileSelect}
                  style={{ display: 'none' }}
                />
                {selectedFile && (
                  <p className="file-name">{selectedFile.name}</p>
                )}
              </div>
            )}
          </div>

          <div className="profile-details">
            {editMode ? (
              <div className="edit-form">
                <div className="form-group">
                  <label>Name:</label>
                  <input
                    type="text"
                    value={profile.name}
                    onChange={(e) => setProfile({...profile, name: e.target.value})}
                    placeholder="Enter your name"
                  />
                </div>

                <div className="form-group">
                  <label>Email:</label>
                  <input
                    type="email"
                    value={profile.email}
                    onChange={(e) => setProfile({...profile, email: e.target.value})}
                    placeholder="Enter your email"
                  />
                </div>

                <div className="form-group">
                  <label>Bio:</label>
                  <textarea
                    value={profile.bio}
                    onChange={(e) => setProfile({...profile, bio: e.target.value})}
                    placeholder="Tell us about yourself..."
                    rows="4"
                    maxLength="500"
                  />
                  <small className="char-count">{profile.bio.length}/500</small>
                </div>

                <div className="form-actions">
                  <button 
                    className="btn btn-primary" 
                    onClick={handleSave}
                    disabled={saving}
                  >
                    {saving ? 'Saving...' : 'Save Changes'}
                  </button>
                  <button 
                    className="btn btn-secondary" 
                    onClick={handleCancel}
                    disabled={saving}
                  >
                    Cancel
                  </button>
                </div>
              </div>
            ) : (
              <div className="profile-info">
                <div className="info-row">
                  <label>Name:</label>
                  <span>{profile.name || 'Not set'}</span>
                </div>

                <div className="info-row">
                  <label>Email:</label>
                  <span>{profile.email || 'Not set'}</span>
                </div>

                <div className="info-row">
                  <label>Bio:</label>
                  <span className="bio-text">
                    {profile.bio || 'No bio added yet'}
                  </span>
                </div>

                <div className="profile-actions">
                  <button 
                    className="btn btn-primary"
                    onClick={() => setEditMode(true)}
                  >
                    Edit Profile
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
