import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './WorkoutDetail.css';

const WorkoutDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { currentUser, isMember } = useAuth();
  const [workout, setWorkout] = useState(null);
  const [signups, setSignups] = useState([]);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [loading, setLoading] = useState(true);
  const [isSignedUp, setIsSignedUp] = useState(false);

  const API_BASE_URL = 'http://localhost:5001/api';

  useEffect(() => {
    if (!currentUser) {
      navigate('/login');
      return;
    }

    if (!isMember(currentUser)) {
      navigate('/login');
      return;
    }

    loadWorkoutDetails();
  }, [currentUser, navigate, isMember, id]);

  const loadWorkoutDetails = async () => {
    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      // Load workout details
      const workoutResponse = await fetch(`${API_BASE_URL}/forum/workouts/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (workoutResponse.ok) {
        const workoutData = await workoutResponse.json();
        setWorkout(workoutData.workout);
      }

      // Load signups (placeholder for now)
      setSignups([
        { id: 1, user_name: 'John Doe', signed_up_at: '2024-01-15' },
        { id: 2, user_name: 'Jane Smith', signed_up_at: '2024-01-15' }
      ]);

      // Load comments (placeholder for now)
      setComments([
        { id: 1, user_name: 'John Doe', content: 'Looking forward to this workout!', created_at: '2024-01-15' },
        { id: 2, user_name: 'Jane Smith', content: 'What should I bring?', created_at: '2024-01-15' }
      ]);

      setLoading(false);
    } catch (error) {
      console.error('Error loading workout details:', error);
      setLoading(false);
    }
  };

  const handleSignUp = async () => {
    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      // Toggle signup status
      if (isSignedUp) {
        // Remove signup
        setSignups(signups.filter(s => s.user_name !== currentUser.name));
        setIsSignedUp(false);
      } else {
        // Add signup
        const newSignup = {
          id: Date.now(),
          user_name: currentUser.name,
          signed_up_at: new Date().toISOString().split('T')[0]
        };
        setSignups([...signups, newSignup]);
        setIsSignedUp(true);
      }
    } catch (error) {
      console.error('Error updating signup:', error);
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;

    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      const newCommentObj = {
        id: Date.now(),
        user_name: currentUser.name,
        content: newComment.trim(),
        created_at: new Date().toISOString().split('T')[0]
      };

      setComments([...comments, newCommentObj]);
      setNewComment('');
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  if (loading) {
    return <div className="loading">Loading workout details...</div>;
  }

  if (!workout) {
    return <div className="error">Workout not found</div>;
  }

  return (
    <div className="workout-detail-container">
      <div className="container">
        <button className="back-btn" onClick={() => navigate('/forum')}>
          ‚Üê Back to Forum
        </button>

        <div className="workout-detail-card">
          <div className="workout-header">
            <h1 className="workout-title">{workout.title}</h1>
            <div className="workout-author">
              Posted by {workout.author_name}
            </div>
          </div>

          {workout.workout_type && (
            <div className="workout-meta">
              <span className="workout-type-badge">{workout.workout_type}</span>
              {workout.workout_date && (
                <span className="workout-date">
                  üìÖ {new Date(workout.workout_date).toLocaleDateString()}
                </span>
              )}
            </div>
          )}

          <div className="workout-content">
            {workout.content}
          </div>

          <div className="workout-actions">
            <button 
              onClick={handleSignUp}
              className={`signup-btn ${isSignedUp ? 'signed-up' : ''}`}
            >
              {isSignedUp ? '‚úì Signed Up' : 'Sign Up'}
            </button>
          </div>
        </div>

        <div className="signups-section">
          <h2>Who's Coming ({signups.length})</h2>
          {signups.length === 0 ? (
            <p className="no-signups">No one has signed up yet. Be the first!</p>
          ) : (
            <div className="signups-list">
              {signups.map(signup => (
                <div key={signup.id} className="signup-item">
                  <span className="signup-user">{signup.user_name}</span>
                  <span className="signup-date">{signup.signed_up_at}</span>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="comments-section">
          <h2>Comments ({comments.length})</h2>

          <form onSubmit={handleCommentSubmit} className="comment-form">
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="Add a comment..."
              rows="3"
              maxLength="500"
              required
            />
            <button type="submit" className="comment-submit-btn">Post Comment</button>
          </form>

          {comments.length === 0 ? (
            <p className="no-comments">No comments yet. Start the conversation!</p>
          ) : (
            <div className="comments-list">
              {comments.map(comment => (
                <div key={comment.id} className="comment-item">
                  <div className="comment-header">
                    <span className="comment-author">{comment.user_name}</span>
                    <span className="comment-date">{comment.created_at}</span>
                  </div>
                  <div className="comment-content">{comment.content}</div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default WorkoutDetail;
