import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const API_BASE_URL = 'http://localhost:5001/api';

  useEffect(() => {
    console.log('ðŸ”„ AuthContext useEffect running...');

    // Check if user is logged in from localStorage (for now)
    const savedUser = localStorage.getItem('triathlonUser');
    console.log('ðŸ“¦ Saved user from localStorage:', savedUser);

    if (savedUser) {
      try {
        const parsedUser = JSON.parse(savedUser);
        console.log('âœ… Parsed user successfully:', parsedUser);
        setCurrentUser(parsedUser);
      } catch (error) {
        console.error('âŒ Error parsing saved user:', error);
        localStorage.removeItem('triathlonUser');
      }
    } else {
      console.log('âŒ No saved user found in localStorage');
    }

    setLoading(false);
    console.log('ðŸ AuthContext loading complete');
  }, []);

  const signup = async (email, password, name, phoneNumber) => {
    try {
      const response = await fetch(`${API_BASE_URL}/auth/signup`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password, name, phoneNumber }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Signup failed');
      }

      const user = await response.json();

      // Store user in localStorage for now (will be replaced with JWT)
      localStorage.setItem('triathlonUser', JSON.stringify(user));
      setCurrentUser(user);
      return user;
    } catch (error) {
      console.error('Signup error:', error);
      throw error;
    }
  };

  const login = async (email, password) => {
    try {
      console.log('ðŸ” Attempting login for:', email);

      const response = await fetch(`${API_BASE_URL}/auth/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Login failed');
      }

      const { user, token } = await response.json();
      console.log('âœ… Login response received:', user);

      // Store user and token
      localStorage.setItem('triathlonUser', JSON.stringify(user));
      localStorage.setItem('triathlonToken', token);
      console.log('ðŸ’¾ User stored in localStorage');

      setCurrentUser(user);
      console.log('ðŸ‘¤ Current user state set to:', user);

      return user;
    } catch (error) {
      console.error('âŒ Login error:', error);
      throw error;
    }
  };

  const logout = () => {
    localStorage.removeItem('triathlonUser');
    localStorage.removeItem('triathlonToken');
    setCurrentUser(null);
  };

  const getUserRole = (user) => {
    if (!user) return 'public';
    return user.role || 'public';
  };

  const hasPermission = (user, requiredRole) => {
    const userRole = getUserRole(user);
    const roleHierarchy = {
      'public': 0,
      'pending': 1,
      'member': 2,
      'exec': 3,
      'administrator': 4
    };

    return roleHierarchy[userRole] >= roleHierarchy[requiredRole];
  };

  const isAdmin = (user) => {
    return hasPermission(user, 'administrator');
  };

  const isExec = (user) => {
    return hasPermission(user, 'exec');
  };

  const isMember = (user) => {
    return hasPermission(user, 'member');
  };

  // Temporary function to promote current user to admin (for development)
  const promoteToAdmin = () => {
    if (currentUser) {
      const updatedUser = { ...currentUser, role: 'administrator' };
      setCurrentUser(updatedUser);
      localStorage.setItem('triathlonUser', JSON.stringify(updatedUser));
      return updatedUser;
    }
    return null;
  };

  // Function to update user profile
  const updateUser = (updatedUserData) => {
    const updatedUser = { ...currentUser, ...updatedUserData };
    setCurrentUser(updatedUser);
    localStorage.setItem('triathlonUser', JSON.stringify(updatedUser));
    return updatedUser;
  };

  const value = {
    currentUser,
    signup,
    login,
    logout,
    loading,
    isAdmin,
    isExec,
    isMember,
    hasPermission,
    getUserRole,
    promoteToAdmin,
    updateUser
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
