import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import './Forum.css';

const Forum = () => {
  const { currentUser, isMember, isExec, getUserRole } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('workouts');
  const [workoutPosts, setWorkoutPosts] = useState([]);
  const [eventPosts, setEventPosts] = useState([]);
  const [newWorkout, setNewWorkout] = useState('');
  const [newEvent, setNewEvent] = useState('');
  const [loading, setLoading] = useState(true);
  const [showWorkoutForm, setShowWorkoutForm] = useState(false);
  const [showEventForm, setShowEventForm] = useState(false);

  const API_BASE_URL = 'http://localhost:5001/api';

  useEffect(() => {
    if (!currentUser) {
      navigate('/login');
      return;
    }

    // Check if user has member access or higher
    if (!isMember(currentUser)) {
      navigate('/login');
      return;
    }

    // Load forum posts from backend API
    loadForumPosts();
  }, [currentUser, navigate, isMember]);

  const loadForumPosts = async () => {
    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      // Load workout posts
      const workoutResponse = await fetch(`${API_BASE_URL}/forum/workouts`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (workoutResponse.ok) {
        const workoutData = await workoutResponse.json();
        setWorkoutPosts(workoutData.posts || []);
      }

      // Load event posts
      const eventResponse = await fetch(`${API_BASE_URL}/forum/events`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (eventResponse.ok) {
        const eventData = await eventResponse.json();
        setEventPosts(eventData.posts || []);
      }
    } catch (error) {
      console.error('Error loading forum posts:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitPost = async (e) => {
    e.preventDefault();
    if (!newPost.trim()) return;

    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      const response = await fetch(`${API_BASE_URL}/forum/posts`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          content: newPost
        })
      });

      if (response.ok) {
        const newPostData = await response.json();
        setPosts([newPostData.post, ...posts]);
        setNewPost('');
      } else {
        console.error('Failed to create post');
      }
    } catch (error) {
      console.error('Error creating post:', error);
    }
  };

  const handleLike = async (postId) => {
    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      const response = await fetch(`${API_BASE_URL}/forum/posts/${postId}/like`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        // Reload posts to get updated like count
        loadForumPosts();
      } else {
        console.error('Failed to like post');
      }
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!currentUser) {
    return null;
  }

  return (
    <div className="forum-container">
      <div className="container">
        <h1 className="section-title">Team Forum</h1>
        <p className="section-subtitle">Connect with your teammates and discuss training, races, and more!</p>

        {/* Forum Tabs */}
        <div className="forum-tabs">
          <button 
            className={`tab-button ${activeTab === 'workouts' ? 'active' : ''}`}
            onClick={() => setActiveTab('workouts')}
          >
            🏃‍♂️ Workouts
          </button>
          <button 
            className={`tab-button ${activeTab === 'events' ? 'active' : ''}`}
            onClick={() => setActiveTab('events')}
          >
            🎉 Events
          </button>
        </div>

        {/* Workouts Section */}
        {activeTab === 'workouts' && (
          <div className="workouts-section">
            <div className="section-header">
              <h2>Workout Posts</h2>
              {isExec(currentUser) && (
                <button 
                  className="new-post-btn"
                  onClick={() => setShowWorkoutForm(true)}
                >
                  + New Workout
                </button>
              )}
            </div>

            {workoutPosts.length === 0 ? (
              <p className="no-posts">No workout posts yet.</p>
            ) : (
              <div className="posts-list">
                {workoutPosts.map(post => (
                  <div key={post.id} className="post-card workout-post">
                    <div className="post-header">
                      <div className="post-author">
                        <div className="author-avatar">
                          {post.author_name.charAt(0).toUpperCase()}
                        </div>
                        <div className="author-info">
                          <span className="author-name">{post.author_name}</span>
                          <span className="post-time">
                            {new Date(post.created_at).toLocaleDateString()}
                          </span>
                        </div>
                      </div>
                      <span className="post-type">Workout</span>
                    </div>
                    <div className="post-content">{post.content}</div>
                    <div className="post-footer">
                      <button 
                        onClick={() => handleLike(post.id)}
                        className="like-btn"
                      >
                        👍 {post.likes || 0}
                      </button>
                      <button className="signup-btn">Sign Up</button>
                      <button className="comment-btn">Comment</button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {/* Events Section */}
        {activeTab === 'events' && (
          <div className="events-section">
            <div className="section-header">
              <h2>Event Posts</h2>
              {isExec(currentUser) && (
                <button 
                  className="new-post-btn"
                  onClick={() => setShowEventForm(true)}
                >
                  + New Event
                </button>
              )}
            </div>

            {eventPosts.length === 0 ? (
              <p className="no-posts">No event posts yet.</p>
            ) : (
              <div className="posts-list">
                {eventPosts.map(post => (
                  <div key={post.id} className="post-card event-post">
                    <div className="post-header">
                      <div className="post-author">
                        <div className="author-avatar">
                          {post.author_name.charAt(0).toUpperCase()}
                        </div>
                        <div className="author-info">
                          <span className="author-name">{post.author_name}</span>
                          <span className="post-time">
                            {new Date(post.created_at).toLocaleDateString()}
                          </span>
                        </div>
                      </div>
                      <span className="post-type">Event</span>
                    </div>
                    <div className="post-content">{post.content}</div>
                    <div className="post-footer">
                      <button 
                        onClick={() => handleLike(post.id)}
                        className="like-btn"
                      >
                        👍 {post.likes || 0}
                      </button>
                      <div className="rsvp-buttons">
                        <button className="rsvp-btn going">Going</button>
                        <button className="rsvp-btn maybe">Maybe</button>
                        <button className="rsvp-btn not-going">Not Going</button>
                      </div>
                      <button className="comment-btn">Comment</button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Forum;
