import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './ResetPassword.css';

const ResetPassword = () => {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const { login } = useAuth();
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [loading, setLoading] = useState(false);
  const [userEmail, setUserEmail] = useState('');

  const token = searchParams.get('token');

  useEffect(() => {
    if (!token) {
      setError('Invalid reset link. Please request a new password reset.');
    }
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!token) {
      setError('Invalid reset link. Please request a new password reset.');
      return;
    }

    if (newPassword !== confirmPassword) {
      setError('Passwords do not match. Please ensure both password fields are identical.');
      return;
    }

    if (newPassword.length < 6) {
      setError('Password must be at least 6 characters long.');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const requestBody = { 
        token: token,
        newPassword: newPassword 
      };
      console.log('üîë Sending reset password request:', requestBody);

      const response = await fetch('http://localhost:5001/api/auth/reset-password', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      if (response.ok) {
        setSuccess(true);
        setNewPassword('');
        setConfirmPassword('');

        // Try to get user email from the token and auto-login
        try {
          const emailResponse = await fetch(`http://localhost:5001/api/auth/get-user-email?token=${token}`);
          if (emailResponse.ok) {
            const { email } = await emailResponse.json();
            setUserEmail(email);

            // Auto-login the user
            await login(email, newPassword);
            console.log('‚úÖ Auto-login successful after password reset');
          }
        } catch (loginError) {
          console.log('‚ö†Ô∏è Auto-login failed, user will need to login manually:', loginError);
        }
      } else {
        const errorData = await response.json();
        setError(errorData.error || 'Failed to reset password');
      }
    } catch (error) {
      setError('Failed to reset password. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleGoToLogin = () => {
    navigate('/login');
  };

  if (!token) {
    return (
      <div className="reset-password-container">
        <div className="reset-password-card">
          <h2>Invalid Reset Link</h2>
          <p className="error-message">
            This password reset link is invalid or has expired. 
            Please request a new password reset from the login page.
          </p>
          <button onClick={handleGoToLogin} className="btn btn-primary">
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  if (success) {
    return (
      <div className="reset-password-container">
        <div className="reset-password-card">
          <h2>Password Reset Successful!</h2>
          <div className="success-message">
            <p>‚úÖ Your password has been reset successfully!</p>
            {userEmail ? (
              <>
                <p>üéâ You have been automatically logged in!</p>
                <p>Redirecting you to the home page...</p>
              </>
            ) : (
              <p>You can now log in with your new password.</p>
            )}
          </div>
          {userEmail ? (
            <button onClick={() => navigate('/')} className="btn btn-primary">
              Go to Home
            </button>
          ) : (
            <button onClick={handleGoToLogin} className="btn btn-primary">
              Go to Login
            </button>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="reset-password-container">
      <div className="reset-password-card">
        <h2>Reset Your Password</h2>
        <p className="reset-instructions">
          Enter your new password below. Make sure it's secure and easy to remember.
        </p>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="newPassword">New Password</label>
            <input
              type="password"
              id="newPassword"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
              placeholder="Enter your new password"
              minLength="6"
            />
          </div>

          <div className="form-group">
            <label htmlFor="confirmPassword">Confirm New Password</label>
            <input
              type="password"
              id="confirmPassword"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
              placeholder="Confirm your new password"
              minLength="6"
            />
            {confirmPassword && (
              <div className={`password-validation ${newPassword === confirmPassword ? 'valid' : 'invalid'}`}>
                {newPassword === confirmPassword ? '‚úÖ Passwords match' : '‚ùå Passwords do not match'}
              </div>
            )}
          </div>

          <button 
            type="submit" 
            className="submit-btn" 
            disabled={loading || !newPassword || !confirmPassword}
          >
            {loading ? 'Resetting Password...' : 'Reset Password'}
          </button>
        </form>

        <div className="back-to-login">
          <p>
            Remember your password?{' '}
            <button type="button" onClick={handleGoToLogin} className="link-btn">
              Sign In
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default ResetPassword;
