import React, { useState, useEffect, useContext } from 'react';
import { useAuth } from '../context/AuthContext';
import './Admin.css';

const Admin = () => {
  const { currentUser, isAdmin } = useAuth();
  const [members, setMembers] = useState([]);
  const [pendingMembers, setPendingMembers] = useState([]);
  const [activeTab, setActiveTab] = useState('overview');
  const [loginHistory, setLoginHistory] = useState([]);
  const [stats, setStats] = useState({
    totalMembers: 0,
    pendingMembers: 0,
    activeToday: 0,
    newThisWeek: 0
  });

  const API_BASE_URL = 'http://localhost:5001/api';

  // Load data from backend API
  useEffect(() => {
    if (!currentUser || !isAdmin(currentUser)) {
      return;
    }

    loadAdminData();
  }, [currentUser, isAdmin]);

  const loadAdminData = async () => {
    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      // Load admin stats
      const statsResponse = await fetch(`${API_BASE_URL}/admin/stats`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (statsResponse.ok) {
        const statsData = await statsResponse.json();
        setStats(statsData.stats);
      }

      // Load all members
      const membersResponse = await fetch(`${API_BASE_URL}/admin/members`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (membersResponse.ok) {
        const membersData = await membersResponse.json();
        setMembers(membersData.members);

        // Filter pending members
        const pending = membersData.members.filter(m => m.role === 'pending');
        setPendingMembers(pending);
      }

      // Load login history
      const historyResponse = await fetch(`${API_BASE_URL}/admin/login-history`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (historyResponse.ok) {
        const historyData = await historyResponse.json();
        setLoginHistory(historyData.history);
      }
    } catch (error) {
      console.error('Error loading admin data:', error);
    }
  };

  const approveMember = async (memberId) => {
    try {
      const token = localStorage.getItem('triathlonToken');
      const response = await fetch(`${API_BASE_URL}/admin/members/${memberId}/approve`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ role: 'member' })
      });

      if (response.ok) {
        // Reload data to get updated information
        loadAdminData();
      } else {
        console.error('Failed to approve member');
      }
    } catch (error) {
      console.error('Error approving member:', error);
    }
  };

  const rejectMember = async (memberId) => {
    try {
      const token = localStorage.getItem('triathlonToken');
      const response = await fetch(`${API_BASE_URL}/admin/members/${memberId}/reject`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ role: 'rejected' })
      });

      if (response.ok) {
        // Reload data to get updated information
        loadAdminData();
      } else {
        console.error('Failed to reject member');
      }
    } catch (error) {
      console.error('Error rejecting member:', error);
    }
  };

  const editExpiryDate = async (memberId) => {
    const newExpiryDate = prompt('Enter new expiry date (YYYY-MM-DD):');
    if (!newExpiryDate) return;

    try {
      const token = localStorage.getItem('triathlonToken');
      const response = await fetch(`${API_BASE_URL}/admin/members/${memberId}/expiry`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ expiryDate: newExpiryDate })
      });

      if (response.ok) {
        // Reload data to get updated information
        loadAdminData();
      } else {
        console.error('Failed to update expiry date');
      }
    } catch (error) {
      console.error('Error updating expiry date:', error);
    }
  };

  const getStatusBadge = (status) => {
    const statusClasses = {
      approved: 'status-approved',
      pending: 'status-pending',
      rejected: 'status-rejected'
    };
    return <span className={`status-badge ${statusClasses[status]}`}>{status}</span>;
  };

  // Debug: Log current user info
  console.log('Current user:', currentUser);
  console.log('Is admin check:', isAdmin(currentUser));

  // Check if current user is admin
  if (!currentUser || !isAdmin(currentUser)) {
    return (
      <div className="admin-container">
        <div className="admin-access-denied">
          <h2>Access Denied</h2>
          <p>You don't have permission to access the admin panel.</p>
          <p>Current user: {currentUser ? currentUser.email : 'None'}</p>
          <p>User role: {currentUser ? currentUser.role : 'None'}</p>
          <p>Please contact the club administrators if you believe this is an error.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-container">
      <div className="admin-header">
        <h1>Admin Dashboard</h1>
        <p>Manage club members and monitor activity</p>
      </div>

      <div className="admin-tabs">
        <button 
          className={`tab-button ${activeTab === 'overview' ? 'active' : ''}`}
          onClick={() => setActiveTab('overview')}
        >
          Overview
        </button>
        <button 
          className={`tab-button ${activeTab === 'members' ? 'active' : ''}`}
          onClick={() => setActiveTab('members')}
        >
          All Members
        </button>
        <button 
          className={`tab-button ${activeTab === 'pending' ? 'active' : ''}`}
          onClick={() => setActiveTab('pending')}
        >
          Pending Approval
        </button>
        <button 
          className={`tab-button ${activeTab === 'activity' ? 'active' : ''}`}
          onClick={() => setActiveTab('activity')}
        >
          Login Activity
        </button>
      </div>

      <div className="admin-content">
        {activeTab === 'overview' && (
          <div className="overview-section">
            <div className="stats-grid">
              <div className="stat-card">
                <h3>Total Members</h3>
                <p className="stat-number">{stats.totalMembers}</p>
                <p className="stat-label">Registered users</p>
              </div>
              <div className="stat-card">
                <h3>Pending Approval</h3>
                <p className="stat-number">{stats.pendingMembers}</p>
                <p className="stat-label">Awaiting review</p>
              </div>
              <div className="stat-card">
                <h3>Active Today</h3>
                <p className="stat-number">{stats.activeToday}</p>
                <p className="stat-label">Logged in today</p>
              </div>
              <div className="stat-card">
                <h3>New This Week</h3>
                <p className="stat-number">{stats.newThisWeek}</p>
                <p className="stat-label">Joined recently</p>
              </div>
            </div>

            <div className="quick-actions">
              <h3>Quick Actions</h3>
              <div className="action-buttons">
                <button className="action-btn primary">Export Member List</button>
                <button className="action-btn secondary">Send Mass Email</button>
                <button className="action-btn secondary">Generate Report</button>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'members' && (
          <div className="members-section">
            <h2>All Members</h2>
            <div className="members-table">
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Status</th>
                    <th>Role</th>
                    <th>Join Date</th>
                    <th>Expiry Date</th>
                    <th>Last Login</th>
                    <th>Membership Type</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {members.map(member => (
                    <tr key={member.id}>
                      <td>{member.name}</td>
                      <td>{member.email}</td>
                      <td>{getStatusBadge(member.status)}</td>
                      <td><span className="role-badge">{member.role}</span></td>
                      <td>{member.joinDate}</td>
                      <td>{member.expiryDate || 'Not set'}</td>
                      <td>{member.lastLogin || 'Never'}</td>
                      <td>{member.membershipType}</td>
                      <td>
                        <button className="action-btn small" onClick={() => editExpiryDate(member.id)}>Edit Expiry</button>
                        <button className="action-btn small danger">Remove</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {activeTab === 'pending' && (
          <div className="pending-section">
            <h2>Pending Approval</h2>
            {pendingMembers.length === 0 ? (
              <p className="no-pending">No pending members to approve!</p>
            ) : (
              <div className="pending-grid">
                {pendingMembers.map(member => (
                  <div key={member.id} className="pending-card">
                    <div className="member-info">
                      <h3>{member.name}</h3>
                      <p><strong>Email:</strong> {member.email}</p>
                      <p><strong>Role:</strong> <span className="role-badge">{member.role}</span></p>
                      <p><strong>Join Date:</strong> {member.joinDate}</p>
                      <p><strong>Expiry Date:</strong> {member.expiryDate || 'Not set'}</p>
                      <p><strong>Membership Type:</strong> {member.membershipType}</p>
                    </div>
                    <div className="approval-actions">
                      <button 
                        className="approve-btn"
                        onClick={() => approveMember(member.id)}
                      >
                        ✅ Approve
                      </button>
                      <button 
                        className="reject-btn"
                        onClick={() => rejectMember(member.id)}
                      >
                        ❌ Reject
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'activity' && (
          <div className="activity-section">
            <h2>Login Activity</h2>
            <div className="activity-table">
              <table>
                <thead>
                  <tr>
                    <th>User</th>
                    <th>Login Time</th>
                    <th>IP Address</th>
                    <th>Session Duration</th>
                  </tr>
                </thead>
                <tbody>
                  {loginHistory.map(activity => (
                    <tr key={activity.id}>
                      <td>{activity.userName}</td>
                      <td>{activity.loginTime}</td>
                      <td>{activity.ipAddress}</td>
                      <td>--</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Admin;
