import React, { useState, useEffect, useContext } from 'react';
import { useAuth } from '../context/AuthContext';
import './Admin.css';

const Admin = () => {
  const { currentUser, isAdmin } = useAuth();
  const [members, setMembers] = useState([]);
  const [pendingMembers, setPendingMembers] = useState([]);
  const [activeTab, setActiveTab] = useState('members');

  const [editingMember, setEditingMember] = useState(null);
  const [editForm, setEditForm] = useState({
    name: '',
    email: '',
    role: '',
    expiryDate: '',
    phoneNumber: '',
    charterAccepted: false
  });
  const [approvingMember, setApprovingMember] = useState(null);
  const [approvalForm, setApprovalForm] = useState({
    role: 'member',
    expiryDate: ''
  });

  const API_BASE_URL = 'http://localhost:5001/api';

  // Load data from backend API
  useEffect(() => {
    if (!currentUser || !isAdmin(currentUser)) {
      return;
    }

    loadAdminData();
  }, [currentUser, isAdmin]);

  const loadAdminData = async () => {
    try {
      const token = localStorage.getItem('triathlonToken');
      if (!token) {
        console.error('No authentication token found');
        return;
      }

      // Load all members
      const membersResponse = await fetch(`${API_BASE_URL}/admin/members`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (membersResponse.ok) {
        const membersData = await membersResponse.json();
        console.log('ðŸ“Š Members data received:', membersData.members);

        // Transform backend data to frontend format (snake_case to camelCase)
        const transformedMembers = membersData.members.map(member => ({
          ...member,
          joinDate: member.join_date,
          expiryDate: member.expiry_date,
          paymentConfirmed: member.payment_confirmed,
          absences: member.absences || 0,
          charterAccepted: member.charter_accepted || 0
        }));

        console.log('ðŸ”„ Transformed members data:', transformedMembers);
        console.log('ðŸ“Š Raw backend data:', membersData.members);
        console.log('ðŸ” Sample member with absences:', transformedMembers.find(m => m.id)?.absences);
        console.log('ðŸ” Sample member with charter:', transformedMembers.find(m => m.id)?.charterAccepted);
        console.log('ðŸ” Raw charter_accepted values:', membersData.members.map(m => ({ id: m.id, charter_accepted: m.charter_accepted })));
        console.log('ðŸ” Full transformed members:', JSON.stringify(transformedMembers, null, 2));
        setMembers(transformedMembers);

        // Filter pending members
        const pending = transformedMembers.filter(m => m.role === 'pending');
        setPendingMembers(pending);
      }
    } catch (error) {
      console.error('Error loading admin data:', error);
    }
  };

  const approveMember = (member) => {
    setApprovingMember(member);
    setApprovalForm({
      role: 'member',
      expiryDate: ''
    });
  };

  const handleApprovalSubmit = async () => {
    if (!approvingMember || !approvalForm.expiryDate) {
      alert('Please set an expiry date before approving the member.');
      return;
    }

    try {
      const token = localStorage.getItem('triathlonToken');
      const response = await fetch(`${API_BASE_URL}/admin/members/${approvingMember.id}/approve`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ 
          role: approvalForm.role,
          expiryDate: approvalForm.expiryDate
        })
      });

      if (response.ok) {
        // Reload data to get updated information
        await loadAdminData();
        setApprovingMember(null);
        setApprovalForm({ role: 'member', expiryDate: '' });
      } else {
        console.error('Failed to approve member');
      }
    } catch (error) {
      console.error('Error approving member:', error);
    }
  };

  const cancelApproval = () => {
    setApprovingMember(null);
    setApprovalForm({ role: 'member', expiryDate: '' });
  };



  const removeMember = async (memberId) => {
    const confirmed = window.confirm('âš ï¸ WARNING: This will PERMANENTLY DELETE this user and all their data!\n\nThis action cannot be undone. Are you sure you want to continue?');
    if (!confirmed) return;

    try {
      const token = localStorage.getItem('triathlonToken');
      const response = await fetch(`${API_BASE_URL}/admin/members/${memberId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        console.log('âœ… User deleted successfully');
        await loadAdminData();
      } else {
        console.error('Failed to delete user');
        const err = await response.json().catch(() => ({}));
        alert(`Failed to delete user${err.error ? `: ${err.error}` : ''}`);
      }
    } catch (error) {
      console.error('Error deleting user:', error);
      alert(`Error deleting user: ${error.message}`);
    }
  };

  const rejectMember = async (memberId) => {
    try {
      const token = localStorage.getItem('triathlonToken');
      const response = await fetch(`${API_BASE_URL}/admin/members/${memberId}/reject`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ role: 'rejected' })
      });

      if (response.ok) {
        // Reload data to get updated information
        loadAdminData();
      } else {
        console.error('Failed to reject member');
      }
    } catch (error) {
      console.error('Error rejecting member:', error);
    }
  };

  const editMember = (member) => {
    console.log('ðŸ”„ Editing member:', member);
    console.log('ðŸ” Member charterAccepted value:', member.charterAccepted);

    const initialCharterAccepted = member.charterAccepted || false;
    console.log('ðŸ” Initial charterAccepted value:', initialCharterAccepted);

    setEditingMember(member);
    setEditForm({
      name: member.name,
      email: member.email,
      role: member.role,
      expiryDate: member.expiryDate || '',
      phoneNumber: member.phone_number || '',
      charterAccepted: initialCharterAccepted
    });
    console.log('ðŸ“ Edit form set to:', {
      name: member.name,
      email: member.email,
      role: member.role,
      expiryDate: member.expiryDate || '',
      phoneNumber: member.phone_number || '',
      charterAccepted: initialCharterAccepted
    });
  };

  const saveMemberEdit = async () => {
    console.log('ðŸš¨ saveMemberEdit function called!');
    if (!editingMember) {
      console.log('âŒ No editingMember, returning early');
      return;
    }

    console.log('ðŸ”„ Saving member edit for:', editingMember.id);
    console.log('ðŸ“ Form data:', editForm);

    // Clean up the form data - convert empty strings to null for optional fields
    const cleanFormData = {
      ...editForm,
      expiryDate: editForm.expiryDate || null,
      charterAccepted: editForm.charterAccepted ? 1 : 0
    };

    console.log('ðŸ§¹ Cleaned form data:', cleanFormData);
    console.log('ðŸ” Charter accepted value being sent:', editForm.charterAccepted, 'â†’', cleanFormData.charterAccepted);

    try {
      const token = localStorage.getItem('triathlonToken');
      console.log('ðŸ”‘ Token:', token ? 'Present' : 'Missing');

      const response = await fetch(`${API_BASE_URL}/admin/members/${editingMember.id}/update`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(cleanFormData)
      });

      console.log('ðŸ“¡ Response status:', response.status);

      if (response.ok) {
        const result = await response.json();
        console.log('âœ… Update successful:', result);

        // Check if role was changed and notify the user
        if (editForm.role !== editingMember.role) {
          console.log('ðŸ”„ Role changed from', editingMember.role, 'to', editForm.role);

          // Send notification to the user about role change
          try {
            const notifyResponse = await fetch(`${API_BASE_URL}/admin/notify-role-change`, {
              method: 'POST',
              headers: {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json'
              },
              body: JSON.stringify({
                userId: editingMember.id,
                oldRole: editingMember.role,
                newRole: editForm.role
              })
            });

            if (notifyResponse.ok) {
              console.log('âœ… Role change notification sent');
            } else {
              console.log('âš ï¸ Failed to send role change notification');
            }
          } catch (error) {
            console.log('âš ï¸ Error sending role change notification:', error);
          }
        }

        console.log('ðŸ”„ Reloading admin data...');

        // Reload data to get updated information
        await loadAdminData();
        console.log('âœ… Admin data reloaded');

        setEditingMember(null);
        setEditForm({
          name: '',
          email: '',
          role: '',
          expiryDate: '',
          phoneNumber: '',
          charterAccepted: false
        });
      } else {
        const errorData = await response.json();
        console.error('âŒ Failed to update member:', errorData);
        alert(`Failed to update member: ${errorData.error || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('âŒ Error updating member:', error);
      alert(`Error updating member: ${error.message}`);
    }
  };

  const cancelEdit = () => {
    setEditingMember(null);
    setEditForm({
      name: '',
      email: '',
      role: '',
      expiryDate: '',
      phoneNumber: '',
      charterAccepted: false
    });
  };



  // Debug: Log current user info
  console.log('Current user:', currentUser);
  console.log('Is admin check:', isAdmin(currentUser));

  // Check if current user is admin
  if (!currentUser || !isAdmin(currentUser)) {
    return (
      <div className="admin-container">
        <div className="admin-access-denied">
          <h2>Access Denied</h2>
          <p>You don't have permission to access the admin panel.</p>
          <p>Current user: {currentUser ? currentUser.email : 'None'}</p>
          <p>User role: {currentUser ? currentUser.role : 'None'}</p>
          <p>Please contact the club administrators if you believe this is an error.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-container">
              <div className="admin-header">
          <h1>Admin Dashboard</h1>
          <p>Manage club members and monitor activity</p>

        </div>

      <div className="admin-tabs">
        <button 
          className={`tab-button ${activeTab === 'members' ? 'active' : ''}`}
          onClick={() => setActiveTab('members')}
        >
          All Members
        </button>
        <button 
          className={`tab-button ${activeTab === 'pending' ? 'active' : ''}`}
          onClick={() => setActiveTab('pending')}
        >
          Pending Approval
        </button>
      </div>

      <div className="admin-content">


        {activeTab === 'members' && (
                      <div className="members-section">
              <h2>All Members</h2>
              <div className="admin-warning">
                <p><strong>âš ï¸ Important:</strong> The "Delete" button will permanently remove users and all their data. This action cannot be undone.</p>
              </div>
              <div className="members-table">
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Role</th>
                    <th>Phone Number</th>
                    <th>Join Date</th>
                    <th>Expiry Date</th>
                    <th>Absences</th>
                    <th>Charter Accepted</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {members.map(member => (
                    <tr key={member.id}>
                      <td>{member.name}</td>
                      <td>{member.email}</td>
                      <td><span className={`role-badge ${member.role}`}>{member.role}</span></td>
                      <td>{member.phone_number || 'Not set'}</td>
                      <td>{member.joinDate}</td>
                      <td>{member.expiryDate || 'Not set'}</td>
                      <td>
                        <span className={`absence-count ${member.absences > 0 ? 'has-absences' : 'no-absences'}`}>
                          {member.absences || 0}
                        </span>
                      </td>
                      <td>
                        <span className={`charter-status ${member.charterAccepted ? 'accepted' : 'not-accepted'}`}>
                          {member.charterAccepted ? 'âœ… Yes' : 'âŒ No'}
                        </span>
                      </td>

                      <td>
                        <button className="action-btn small" onClick={() => editMember(member)}>Edit</button>
                        <button className="action-btn small danger" onClick={() => removeMember(member.id)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {activeTab === 'pending' && (
          <div className="pending-section">
            <h2>Pending Approval</h2>
            {pendingMembers.length === 0 ? (
              <p className="no-pending">No pending members to approve!</p>
            ) : (
              <div className="pending-grid">
                {pendingMembers.map(member => (
                  <div key={member.id} className="pending-card">
                    <div className="member-info">
                      <h3>{member.name}</h3>
                      <p><strong>Email:</strong> {member.email}</p>
                      <p><strong>Role:</strong> <span className="role-badge">{member.role}</span></p>
                      <p><strong>Phone Number:</strong> {member.phone_number || 'Not set'}</p>
                      <p><strong>Join Date:</strong> {member.joinDate}</p>
                      <p><strong>Expiry Date:</strong> {member.expiryDate || 'Not set'}</p>

                    </div>
                    <div className="approval-actions">
                                              <button 
                          className="approve-btn"
                          onClick={() => approveMember(member)}
                        >
                          âœ… Approve
                        </button>
                      <button 
                        className="reject-btn"
                        onClick={() => rejectMember(member.id)}
                      >
                        âŒ Reject
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}


      </div>

      {/* Edit Member Modal */}
      {editingMember && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Edit Member: {editingMember.name}</h2>
            {console.log('ðŸ” Modal rendering with editForm:', editForm)}
            <form onSubmit={(e) => { e.preventDefault(); saveMemberEdit(); }}>
              <div className="form-group">
                <label>Name:</label>
                <input
                  type="text"
                  value={editForm.name}
                  onChange={(e) => setEditForm({...editForm, name: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Email:</label>
                <input
                  type="email"
                  value={editForm.email}
                  onChange={(e) => setEditForm({...editForm, email: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Role:</label>
                <select
                  value={editForm.role}
                  onChange={(e) => setEditForm({...editForm, role: e.target.value})}
                  required
                >
                  <option value="pending">Pending</option>
                  <option value="member">Member</option>
                  <option value="exec">Executive</option>
                  <option value="administrator">Administrator</option>
                </select>
              </div>

              <div className="form-group">
                <label>Expiry Date:</label>
                <input
                  type="date"
                  value={editForm.expiryDate}
                  onChange={(e) => setEditForm({...editForm, expiryDate: e.target.value})}
                />
              </div>

              <div className="form-group">
                <label>Phone Number:</label>
                <input
                  type="tel"
                  value={editForm.phoneNumber}
                  onChange={(e) => setEditForm({...editForm, phoneNumber: e.target.value})}
                  placeholder="+1 (555) 123-4567"
                  pattern="[\+]?[1-9][\d]{0,15}"
                />
                <small>For SMS notifications when promoted from waitlists</small>
              </div>

              <div className="form-group">
                <label>Charter Accepted:</label>
                <select
                  value={editForm.charterAccepted ? '1' : '0'}
                  onChange={(e) => {
                    const newValue = e.target.value === '1';
                    console.log('ðŸ” Charter dropdown changed:', e.target.value, 'â†’', newValue);
                    setEditForm({...editForm, charterAccepted: newValue});
                  }}
                >
                  <option value="0">No</option>
                  <option value="1">Yes</option>
                </select>
                <small>Whether the member has accepted the team charter</small>
                <div style={{fontSize: '0.8em', color: '#666', marginTop: '4px'}}>
                  Current value: {editForm.charterAccepted ? 'Yes (1)' : 'No (0)'}
                </div>
              </div>
              <div className="modal-actions">
                <button type="submit" className="btn btn-primary">Save Changes</button>
                <button type="button" className="btn btn-secondary" onClick={cancelEdit}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Approval Modal */}
      {approvingMember && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Approve Member: {approvingMember.name}</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleApprovalSubmit(); }}>
              <div className="form-group">
                <label>Role:</label>
                <select
                  value={approvalForm.role}
                  onChange={(e) => setApprovalForm({...approvalForm, role: e.target.value})}
                  required
                >
                  <option value="member">Member</option>
                  <option value="exec">Executive</option>
                  <option value="administrator">Administrator</option>
                </select>
              </div>

              <div className="form-group">
                <label>Expiry Date:</label>
                <input
                  type="date"
                  value={approvalForm.expiryDate}
                  onChange={(e) => setApprovalForm({...approvalForm, expiryDate: e.target.value})}
                  required
                />
                <small>Membership will expire on this date</small>
              </div>

              <div className="modal-actions">
                <button type="submit" className="btn btn-primary">Approve Member</button>
                <button type="button" className="btn btn-secondary" onClick={cancelApproval}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Admin;
