import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const API_BASE_URL = 'http://localhost:5001/api';

  useEffect(() => {
    // Check if user is logged in from localStorage (for now)
    const savedUser = localStorage.getItem('triathlonUser');
    if (savedUser) {
      try {
        setCurrentUser(JSON.parse(savedUser));
      } catch (error) {
        console.error('Error parsing saved user:', error);
        localStorage.removeItem('triathlonUser');
      }
    }
    setLoading(false);
  }, []);

  const signup = (email, password, name) => {
    // In a real app, this would make an API call
    const user = {
      id: Date.now().toString(),
      email,
      name,
      role: 'pending', // New users start as pending
      createdAt: new Date().toISOString()
    };

    // Auto-promote specific users to administrator
    if (email === 'jade.clement@mail.utoronto.ca' || email === 'admin@uofttriathlon.com') {
      user.role = 'administrator';
    }

    localStorage.setItem('triathlonUser', JSON.stringify(user));
    setCurrentUser(user);
    return user;
  };

  const login = (email, password) => {
    // In a real app, this would validate credentials
    const savedUser = localStorage.getItem('triathlonUser');
    if (savedUser) {
      const user = JSON.parse(savedUser);
      if (user.email === email) {
        setCurrentUser(user);

        // Track login activity
        const loginActivity = {
          userId: user.id || user.email,
          userName: user.name || user.email,
          loginTime: new Date().toISOString(),
          ipAddress: 'N/A' // In a real app, you'd get this from the server
        };

        const existingActivity = JSON.parse(localStorage.getItem('loginHistory') || '[]');
        existingActivity.push(loginActivity);
        localStorage.setItem('loginHistory', JSON.stringify(existingActivity));

        return user;
      }
    }
    throw new Error('Invalid credentials');
  };

  const logout = () => {
    localStorage.removeItem('triathlonUser');
    setCurrentUser(null);
  };

  const getUserRole = (user) => {
    if (!user) return 'public';
    return user.role || 'public';
  };

  const hasPermission = (user, requiredRole) => {
    const userRole = getUserRole(user);
    const roleHierarchy = {
      'public': 0,
      'pending': 1,
      'member': 2,
      'exec': 3,
      'administrator': 4
    };

    return roleHierarchy[userRole] >= roleHierarchy[requiredRole];
  };

  const isAdmin = (user) => {
    return hasPermission(user, 'administrator');
  };

  const isExec = (user) => {
    return hasPermission(user, 'exec');
  };

  const isMember = (user) => {
    return hasPermission(user, 'member');
  };

  // Temporary function to promote current user to admin (for development)
  const promoteToAdmin = () => {
    if (currentUser) {
      const updatedUser = { ...currentUser, role: 'administrator' };
      setCurrentUser(updatedUser);
      localStorage.setItem('triathlonUser', JSON.stringify(updatedUser));
      return updatedUser;
    }
    return null;
  };

  const value = {
    currentUser,
    signup,
    login,
    logout,
    loading,
    isAdmin,
    isExec,
    isMember,
    hasPermission,
    getUserRole,
    promoteToAdmin
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
