import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const location = useLocation();

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const closeMenu = () => {
    setIsOpen(false);
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo" onClick={closeMenu}>
          <div className="logo-icon">
            <svg width="40" height="40" viewBox="0 0 40 40" fill="none">
              <text x="20" y="28" textAnchor="middle" fontSize="32" fontWeight="bold" fill="#1E3A8A">T</text>
              <path d="M12 8 L16 8 L16 12 L18 12 L18 16 L16 16 L16 20 L14 20 L14 16 L12 16 Z" fill="#DC2626" stroke="white" strokeWidth="0.5"/>
            </svg>
          </div>
          <div className="logo-text-container">
            <span className="logo-text">UofT</span>
            <span className="logo-sport">TRI</span>
          </div>
        </Link>

        <div className={`navbar-menu ${isOpen ? 'active' : ''}`}>
          <Link 
            to="/" 
            className={`navbar-link ${isActive('/') ? 'active' : ''}`}
            onClick={closeMenu}
          >
            Home
          </Link>
          <Link 
            to="/schedule" 
            className={`navbar-link ${isActive('/schedule') ? 'active' : ''}`}
            onClick={closeMenu}
          >
            Schedule
          </Link>
          <Link 
            to="/coaches-exec" 
            className={`navbar-link ${isActive('/coaches-exec') ? 'active' : ''}`}
            onClick={closeMenu}
          >
            Coaches & Exec
          </Link>
          <Link 
            to="/forum" 
            className={`navbar-link ${isActive('/forum') ? 'active' : ''}`}
            onClick={closeMenu}
          >
            Forum
          </Link>
          <Link 
            to="/login" 
            className={`navbar-link ${isActive('/login') ? 'active' : ''}`}
            onClick={closeMenu}
          >
            Login
          </Link>
        </div>

        <div className="navbar-toggle" onClick={toggleMenu}>
          <span className={`bar ${isOpen ? 'active' : ''}`}></span>
          <span className={`bar ${isOpen ? 'active' : ''}`}></span>
          <span className={`bar ${isOpen ? 'active' : ''}`}></span>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
